jQuery(document).ready(function (a) {

    if (jQuery("#shipment_tracking_auto_import").val() == 'Disabled')
    {
        jQuery(".shipment_tracking_import_section").hide();
    } else
    {
        jQuery(".shipment_tracking_import_section").show();
    }

    "use strict";
    a("#enable_ftp_ie").click(function () {
        if (this.checked) {
            a("#shipment_tracking_import_section_all").show();
        } else {
            a("#shipment_tracking_import_section_all").hide();
        }
    });

    a("select[name=shipment_tracking_auto_import]").change(function () {
        if ("Disabled" === a(this).val()) {
            a(".shipment_tracking_import_section").hide();
        } else {
            a(".shipment_tracking_import_section").show();
        }
    })
    if (woocommerce_shipment_tracking_csv_cron_params.shipment_tracking_auto_import === 'Disabled') {
        a(".shipment_tracking_import_section").hide();
    }
    if (woocommerce_shipment_tracking_csv_cron_params.enable_ftp_ie != 1) {
        a("#shipment_tracking_import_section_all").hide();
    }
    ;
    jQuery('.read_more').click(function(){
      if (jQuery(this).parent('p').find('#dots').css('display') === "none") {
        jQuery(this).parent('p').find('#dots').css('display','inline');
        jQuery(this).parent('p').find('#more').css('display','none');
        jQuery(this).html('Read More');
      } else {
        jQuery(this).parent('p').find('#dots').css('display','none');
        jQuery(this).html(' Read Less');
        jQuery(this).parent('p').find('#more').css('display','inline');
      }
    });
});